import React, {useState} from 'react';
import {
  Image,
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
} from 'react-native';
import {StackNavigationProp} from '@react-navigation/stack';
import {RootStackParamList} from './navigationTypes';

type SettingsScreenNavigationProp = StackNavigationProp<
  RootStackParamList,
  'SettingsScreen'
>;

interface SettingsScreenProps {
  navigation: SettingsScreenNavigationProp;
}

const SettingsScreen: React.FC<SettingsScreenProps> = ({navigation}) => {
  const [isLoggedIn, setIsLoggedIn] = useState(true);

  const handleLogout = () => {
    setIsLoggedIn(false);
    navigation.replace('Login');
  };

  if (!isLoggedIn) {
    return (
      <View style={styles.container}>
        <Text>User is logged out. Please log in again.</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backButton}>
          <Image
            style={styles.backicon}
            source={{
              uri: 'data:image/png;base64,',
            }}
          />
        </TouchableOpacity>
      </View>

      {/* Body */}
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View>
          {/* Menu Items */}
          <TouchableOpacity
            style={styles.menuItem}
            onPress={() => navigation.navigate('ProfileScreen')}>
            <Image
              style={styles.profile}
              source={{
                uri: 'data:image/png;base64,',
              }}
            />
            <Text style={styles.menuText}>Profile</Text>
            {/* <Image style={styles.profile} source={{uri:"data:image/png;base64,"}}/> */}
          </TouchableOpacity>

          <TouchableOpacity style={styles.menuItem}>
            <Image
              style={styles.profile}
              source={{
                uri: 'data:image/png;base64,',
              }}
            />
            <Text style={styles.menuText}>Orders history</Text>
            {/* <Image style={styles.profile} source={{uri:"data:image/png;base64,"}}/> */}
          </TouchableOpacity>

          <TouchableOpacity style={styles.menuItem}>
            <Image
              style={styles.profile}
              source={{
                uri: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSJQDnF4cAvtdcYSFaPGY1FmFek1kOQfCJvZA&s',
              }}
            />
            <Text style={styles.menuText}>Wallet</Text>
            {/* <Image style={styles.profile} source={{uri:"data:image/png;base64,"}}/> */}
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.menuItem}
            onPress={() => navigation.navigate('NotificationsScreen')}>
            <Image
              style={styles.profile}
              source={{
                uri: 'data:image/png;base64,',
              }}
            />
            <Text style={styles.menuText}>Notifications</Text>
            {/* <Image style={styles.profile} source={{uri:"data:image/png;base64,"}}/> */}
          </TouchableOpacity>

          {/* Logout Button */}
          <TouchableOpacity onPress={handleLogout} style={styles.logoutButton}>
            <Image
              style={styles.profile}
              source={{
                uri: 'data:image/jpeg;base64,',
              }}
            />
            <Text style={styles.logoutText}>Logout</Text>
          </TouchableOpacity>
        </View>

        {/* Footer */}
        <View style={styles.footer}>
          <Text style={styles.footerText}>proposed by</Text>
          <Text style={styles.footerLogo}> WATABE</Text>
        </View>
      </ScrollView>
    </View>
  );
};

